mapper.select:
select `status`.* from car_status_change_record `status` left join car_info car on car.id = `status`.carid where 1=1

    and car.terminal_id=#{name}

    and `status`.current_status <> 0
order by `status`.modify_date desc
limit 0,#{size}

SELECT record.id, record.carid, record.userid, record.prev_status AS prevStatus, record.current_status AS currentStatus, record.modify_date AS modifyDate, record.remark FROM car_status_change_record record LEFT JOIN car_info car ON record.carid = car.id WHERE 1 = 1
AND TO_DAYS(NOW()) - TO_DAYS(record.modify_date) <= #{time}
AND car.carnumber=#{number}
ORDER BY record.modify_date asc

SELECT record.id, record.current_status as `status`, record.modify_date AS createDate FROM car_status_change_record record LEFT JOIN car_info car ON record.carid = car.id WHERE 1 = 1
AND TO_DAYS(NOW()) - TO_DAYS(record.modify_date) <= #{time}
AND car.carnumber=#{number}
ORDER BY record.modify_date asc
