mapper.select:
SELECT car.id, car.deptid, car.carnumber, alarm.alarm_number, alarm.start_lat, alarm.start_lon, alarm.end_lat, alarm.end_lon, alarm.start_time, alarm.end_time, alarm.start_speed, alarm.end_speed , setting.alarmname, IFNULL(alarm.handle_by,'')handle_by FROM car_info car LEFT JOIN device_alarm alarm ON alarm.terminal_id = car.terminal_id LEFT JOIN device_alarm_settings setting ON setting.alarmnumber = alarm.alarm_number left join sys_auth_dept dept on dept.deptid=car.deptid WHERE 1 = 1
AND car.carnumber LIKE CONCAT('%',#{number},'%')
AND alarm.start_time BETWEEN #{startTime} AND #{endTime}
and dept.deptid=#{id}
and alarm.alarm_number=#{type}
order by alarm.end_time desc limit 0,#{size} ;

SELECT car.id, car.deptid, car.carnumber, alarm.alarm_number, alarm.start_lat, alarm.start_lon, alarm.end_lat, alarm.end_lon, alarm.start_time, alarm.end_time, alarm.start_speed, alarm.end_speed , setting.alarmname, IFNULL(alarm.handle_by,'')handle_by FROM car_info car LEFT JOIN device_alarm alarm ON alarm.terminal_id = car.terminal_id LEFT JOIN device_alarm_settings setting ON setting.alarmnumber = alarm.alarm_number left join sys_auth_dept dept on dept.deptid=car.deptid WHERE 1 = 1
AND car.carnumber LIKE CONCAT('%',#{number},'%')
AND alarm.start_time BETWEEN #{startTime} AND #{endTime}
and dept.deptid=#{id}
and alarm.alarm_number ><2 order by alarm.end_time desc limit 0,#{size} ;

SELECT
COUNT(1)
FROM
car_info car
LEFT JOIN device_alarm alarm ON alarm.terminal_id = car.terminal_id
LEFT JOIN device_alarm_settings setting ON setting.alarmnumber = alarm.alarm_number
left join sys_auth_dept dept on dept.deptid=car.deptid
WHERE
1 = 1

    AND car.carnumber LIKE CONCAT('%',#{number},'%')


    AND alarm.start_time BETWEEN #{startTime}
    AND #{endTime}


    and dept.deptid=#{id}


    and alarm.alarm_number=#{type}

;

SELECT COUNT(1) FROM car_info car LEFT JOIN device_alarm alarm ON alarm.terminal_id = car.terminal_id LEFT JOIN device_alarm_settings setting ON setting.alarmnumber = alarm.alarm_number left join sys_auth_dept dept on dept.deptid=car.deptid WHERE 1 = 1 AND TO_DAYS(alarm.start_time)=TO_DAYS(NOW())
and alarm.alarm_number=#{type}
;

SELECT car.carnumber FROM device_alarm alarm LEFT JOIN car_info car ON car.terminal_id = alarm.terminal_id WHERE car.deptid=#{deptid} AND alarm.alarm_number IN (2,14)
AND car.carnumber=#{number}
GROUP BY alarm.terminal_id

SELECT alarm.alarm_number FROM device_alarm alarm LEFT JOIN car_info car ON car.terminal_id = alarm.terminal_id WHERE car.deptid=#{deptid} AND alarm.alarm_number IN (2,14)
AND car.carnumber=#{number}
GROUP BY alarm.alarm_number

SELECT car.id, car.deptid, car.carnumber, `set`.alarmname, alarm.start_lat, alarm.start_lon, alarm.end_lat, alarm.end_lon, alarm.start_time, alarm.end_time, alarm.start_speed, alarm.end_speed FROM car_info car LEFT JOIN device_alarm alarm ON alarm.terminal_id = car.terminal_id LEFT JOIN device_alarm_settings `set` ON `set`.alarmnumber=alarm.alarm_number where 1=1
and car.carnumber=#{number }
and alarm.alarm_number in (2,14)
AND TO_DAYS(NOW())-TO_DAYS(alarm.start_time) <= #{time}
order by alarm.end_time desc

SELECT car.id, car.deptid, car.carnumber, `set`.alarmname, alarm.start_lat, alarm.start_lon, alarm.end_lat, alarm.end_lon, alarm.start_time, alarm.end_time, alarm.start_speed, alarm.end_speed FROM car_info car LEFT JOIN device_alarm alarm ON alarm.terminal_id = car.terminal_id LEFT JOIN device_alarm_settings `set` ON `set`.alarmnumber=alarm.alarm_number where 1=1
and car.carnumber=#{number }
and alarm.alarm_number in (2,14) AND TO_DAYS(NOW()) = TO_DAYS(alarm.start_time) order by alarm.end_time desc
