mapper.select:
SELECT car.id, car.deptid, car.carnumber, alarm.alarm_number, alarm.start_lat, alarm.start_lon, alarm.end_lat, alarm.end_lon, alarm.start_time, alarm.end_time, alarm.start_speed, alarm.end_speed , setting.alarmname, IFNULL(alarm.handle_by,'')handle_by FROM car_info car LEFT JOIN device_alarm alarm ON alarm.terminal_id = car.terminal_id LEFT JOIN device_alarm_settings setting ON setting.alarmnumber = alarm.alarm_number WHERE 1 = 1
AND car.carnumber LIKE CONCAT('%',#{number},'%')
AND alarm.start_time BETWEEN #{startTime} AND #{endTime}
;
