mapper.select:
select * from device_online_record where 1=1

    and terminal_id=#{name}


    and status=#{status}

order by create_date desc
limit 0,#{size}

SELECT record.id, record.terminal_id as terminalId, record.terminal, record.lng, record.lat, record.`status`, record.is_delete as isDelete, record.create_date as createDate FROM device_online_record record LEFT JOIN car_info car ON record.terminal_id = car.terminal_id WHERE 1 = 1
AND TO_DAYS(NOW()) - TO_DAYS(record.create_date) <= #{time}
AND car.carnumber=#{number}
ORDER BY record.create_date asc

SELECT record.id, record.`status` as `status`, record.create_date as createDate FROM device_online_record record LEFT JOIN car_info car ON record.terminal_id = car.terminal_id WHERE 1 = 1
AND TO_DAYS(NOW()) - TO_DAYS(record.create_date) <= #{time}
AND car.carnumber=#{number}
ORDER BY record.create_date asc
