mapper.select:
SELECT i.*, m03.M0331 AS carNumber FROM car_insurance i RIGHT JOIN M03 m03 ON i.carId= m03.RecId WHERE 1 =1
AND m03.MustId=#{MustId}
AND m03.M0331 like #{name}+'%'
and i.carId =#{carid}
and datediff( dd, i.verificationTime, #{verificationTime} ) = 0
ORDER BY i.insuranceId DESC

SELECT CONVERT(varchar(100), verificationTime, 23) FROM car_insurance WHERE 1=1
and carId=#{carId}
GROUP BY CONVERT(varchar(100), verificationTime, 23)

mapper.update:
update car_insurance set
carId=#{carId}

    , annualVerification=#{annualVerification}


    , auditStatus=#{auditStatus}


    , verification=#{verification}


    ,verificationTime=#{verificationTime}


    ,examinant=#{examinant}


    ,jqxId=#{jqxId}


    ,jqxTime=#{jqxTime}


    ,jqxCompany=#{jqxCompany}


    ,jqxMoney=#{jqxMoney}


    ,syxId=#{syxId}


    ,syxTime=#{syxTime}


    ,syxCompany=#{syxCompany}


    , carRestrict=#{carRestrict}


    , insuranceUrl = #{insuranceUrl}


    , syxMoney = #{syxMoney}


    , useRestrict = #{useRestrict}


    , verificationAccessory = #{verificationAccessory}


    , verificationRemark = #{verificationRemark}


    , verificationResult = #{verificationResult}


    , verificationYear = #{verificationYear}

where insuranceId=#{insuranceId}

mapper.select:
SELECT COUNT ( 1 ) FROM car_insurance LEFT JOIN m03 ON car_insurance.carId= m03.recid WHERE 1=1
and car_insurance.auditStatus=#{auditStatus}
and m03.MustId=#{MustId}
