mapper.select:
SELECT p.BeginTime, p.CarNo, p.EndTime, p.LineName, p.PermitNo, t.carType, t.Capacity, t.ExpiryTime, t.CompanyName, t.DriverName, t.DriverMobile, t.DLicenseImage FROM View_PermitTruck p LEFT JOIN View_TruckInfo t ON p.carno = t.carno WHERE 1=1
AND p.CarNo = #{carNo}
and p.EndTime >= #{time}
AND p.permitNo = #{permitNo}
ORDER BY p.EndTime DESC;

select * from (select top ${size} * from (select top ${page} * from ( SELECT top 1000 * FROM ( SELECT p.ProjectId, p.PermitNo, p.LineName, p.ProjectName, pt.EndTime FROM View_ProjectPermit p LEFT JOIN View_PermitTruck pt ON p.PermitNo = pt.PermitNo WHERE 1 = 1
and p.ProjectName LIKE '%'+ #{projectName}+'%'
and pt.EndTime >= #{time}
and (SELECT CONVERT(VARCHAR(10),pt.EndTime,120)) = #{endTime}
and pt.CarNo LIKE '%'+ #{name}+'%'
GROUP BY p.PermitNo, p.ProjectId, p.LineName, p.ProjectName, pt.EndTime ) as a ORDER BY a.EndTime DESC ) as b order by b.EndTime asc ) as temp order by temp.EndTime DESC ) temp_order order by temp_order.EndTime DESC ;

SELECT ISNULL( p.BeginTime, '' ) BeginTime, ISNULL( p.EndTime, '' ) EndTime, ISNULL( p.LineName, '' ) LineName, ISNULL( p.PermitNo, '' ) PermitNo, ISNULL( p.CompanyName, '' ) CompanyName, ISNULL( p.ProjectName , '' ) ProjectName, ISNULL( p.Absorption, '' ) Absorption, ISNULL( i.CarType, '' ) CarType, ISNULL( i.Capacity, '' ) Capacity, ISNULL( i.ExpiryTime, '' ) ExpiryTime, ISNULL( i.CarNo, '' ) CarNo, ISNULL( i.DriverName, '' ) DriverName, ISNULL( i.DriverMobile , '' ) DriverMobile, ISNULL( i.DLicenseImage , '' ) DLicenseImage, ISNULL( p.place, '' ) place, ISNULL( p.mucktype, '' ) mucktype, ISNULL( p.approvetime, '' ) approvetime, ISNULL( p.permittype, '' ) permittype, ISNULL( com.Contract, '' ) Contract, ISNULL( com.Mobile , '' ) Mobile FROM View_PermitInfo p LEFT JOIN View_PermitTruck t ON t.PermitNo= p.PermitNo LEFT JOIN View_TruckInfo i ON i.CarNo= t.CarNo LEFT JOIN View_CompanyInfo com ON com.CompanyName= p.CompanyName WHERE 1 = 1
and t.permitNo= #{projectId}
and t.EndTime >= #{time}
ORDER BY p.EndTime DESC;

SELECT COUNT(*) FROM (SELECT p.PermitNo FROM View_ProjectPermit p LEFT JOIN View_PermitTruck pt ON p.PermitNo = pt.PermitNo WHERE 1 = 1
and p.ProjectName LIKE '%'+ #{projectName}+'%'
and pt.EndTime >= #{time}
and (SELECT CONVERT(VARCHAR(10),pt.EndTime,120)) = #{endTime}
and pt.CarNo LIKE '%'+ #{name}+'%'
GROUP BY p.PermitNo ) AS c

SELECT TOP ${size} * FROM ( SELECT TOP ${total} ISNULL(t.Capacity,'') Capacity, ISNULL(t.CarNo,'')CarNo, ISNULL(t.RecId,'')RecId, ISNULL(t.MustId,'')MustId, ISNULL(t.CarType,'')CarType, ISNULL(t.CarframeNo,'')CarframeNo, ISNULL(t.CompanyName,'')CompanyName, ISNULL(t.DriverCardNo,'')DriverCardNo, ISNULL(t.DriverMobile,'')DriverMobile, ISNULL(t.DriverName,'')DriverName, ISNULL(t.EngineNo,'')EngineNo, ISNULL(t.ExpiryTime,'')ExpiryTime, ISNULL(t.GPSSFYC,'')GPSSFYC, ISNULL(t.OriginalCarNo,'')OriginalCarNo, ISNULL(t.RegisterTime,'')RegisterTime, ISNULL(t.SXSFQQ,'')SXSFQQ, ISNULL( t.JQXYXQ, '' ) JQXYXQ, ISNULL( t.SHYXYXQ, '' ) SHYXYXQ, ISNULL( t.SHCHSHJ, '' ) SHCHSHJ, ISNULL( t.ShortSpell, '' ) ShortSpell, ISNULL(t.ZYZG,'')ZYZG, ISNULL(t.person,'')person, ISNULL(t.quality,'')quality, ISNULL(t.dimensions,'')dimensions, ISNULL(t.scrapTime,'')scrapTime, ISNULL(t.IssuanceDate,'')IssuanceDate, ISNULL( t.totalQuality, '' ) totalQuality, ISNULL( t.checkQuality, '' ) checkQuality, ISNULL(t.tractionQuality,'')tractionQuality, ISNULL( t.stopTransport, '' ) stopTransport, ISNULL(t.stopEndTime,'')stopEndTime, ISNULL(t.DLicenseImage,'')DLicenseImage, ISNULL(t.carRestrict,'')carRestrict, ISNULL(t.useRestrict,'')useRestrict, ISNULL(t.examinant,'')examinant, ISNULL(t.auditStatus,'')auditStatus, ISNULL(t.verificationTime,'')verificationTime FROM View_TruckInfo t WHERE 1=1
and t.CarNo LIKE '%'+ #{carNo} +'%'
and t.CompanyName LIKE '%'+ #{CompanyName} +'%'
and t.DriverName = #{DriverName}
and t.EngineNo = #{EngineNo}
and t.CarframeNo = #{CarframeNo}
and t.CarType = #{CarType}
ORDER BY RegisterTime desc ) a ORDER BY RegisterTime ASC

SELECT TOP ${size} a.* FROM ( SELECT TOP ${total} RecId, M0702, M0703, M0704, M0706, M0707, M0708, M0709, M0710, Area, valid, Dot, VideoUrl, M0718 FROM M07 WHERE 1 = 1
and M0706 LIKE '%'+ #{name}+'%'
and M0702 LIKE '%'+ #{M0702}+'%'
and M0707 LIKE '%'+ #{M0707}+'%'
and M0708 LIKE '%'+ #{M0708}+'%'
and M0703 = #{M0703}
and M0704 = #{M0704}
and M0709 > #{M0709}
and M0710 < #{M0710}
AND datediff( MM, M0709, GETDATE( ) ) < 12 ORDER BY recid DESC ) AS a ORDER BY a.recid ASC;

select * from (SELECT TOP ${size} a.* FROM ( SELECT TOP ${total} RecId, M0417, M0418, M0419, M0401, M0402, M0407, M0408, M0403, M0404, M0405, M0406, M0422, M0409, M0410, M0411, M0412, M0413, M0414, M0420, M0425, M0426, Area, Dot, VideoUrl, videoName, videoPassword, valid, M0429 FROM M04 WHERE 1 = 1
AND M0401 LIKE '%' + #{ name }+ '%'
AND M0417 LIKE '%' + #{ M0417 }+ '%'
AND M0419 = #{ M0419 }
AND M0402 LIKE '%' + #{ M0402 }+ '%'
AND M0403 LIKE '%' + #{ M0403 }+ '%'
AND M0414 = #{ M0414 }
AND M0420 = #{ M0420 }
AND M0425 > #{ M0425 }
AND M0426 < #{ M0426 }
AND datediff( MM, M0418, GETDATE( ) ) < 12 ORDER BY M0418 DESC ) AS a ORDER BY a.M0418 ASC ) AS b ORDER BY b.M0418 DESC;

