SELECT count (1) FROM View_PermitInfo p WHERE 1 = 1
AND p.CompanyName LIKE '%' + #{ name }+ '%'
AND p.ProjectName LIKE '%' + #{ pname }+ '%'
AND p.EndTime = #{BeginTime}
AND p.EndTime between #{time1} and #{time2}
AND CONVERT(nvarchar(10), p.EndTime,102 ) < CONVERT(nvarchar(10),GETDATE(),102)

SELECT userrole.*, users.fullname FROM UserRole LEFT JOIN users ON users.userid= userrole.userid WHERE 1 =1
and userrole.UserId = #{UserId}
and userrole.RoleId = #{RoleId}

SELECT TOP ${size} a.* FROM ( SELECT TOP ${total} ISNULL( View_PermitInfo.BeginTime, '' ) BeginTime, ISNULL( View_PermitInfo.EndTime, '' ) EndTime, ISNULL( View_PermitInfo.LineName, '' ) LineName, ISNULL( View_PermitInfo.PermitNo, '' ) PermitNo, ISNULL( View_PermitInfo.CompanyName, '' ) CompanyName, ISNULL( View_PermitInfo.ProjectName , '' ) ProjectName, ISNULL( View_PermitInfo.Absorption, '' ) Absorption, ISNULL( View_PermitInfo.place, '' ) place, ISNULL( View_PermitInfo.mucktype, '' ) mucktype, ISNULL( View_PermitInfo.approvetime, '' ) approvetime, ISNULL( View_PermitInfo.permittype, '' ) permittype FROM View_PermitInfo LEFT JOIN M06 ON View_PermitInfo.OperObjId=M06.M0601 WHERE 1 = 1
AND View_PermitInfo.CompanyName LIKE '%' + #{ name }+ '%'
AND View_PermitInfo.ProjectName LIKE '%' + #{ pname }+ '%'
AND CONVERT(nvarchar(10), View_PermitInfo.EndTime,112 ) = #{BeginTime}
AND View_PermitInfo.EndTime between #{time1} and #{time2}
AND m06.m0602 = #{ car }
AND CONVERT(nvarchar(10), View_PermitInfo.EndTime,102 ) < CONVERT(nvarchar(10),GETDATE(),121)
ORDER BY View_PermitInfo.EndTime DESC ) a ORDER BY a.EndTime ASC

