mapper.select:
SELECT p.BeginTime, p.CarNo, p.EndTime, p.LineName, p.PermitNo, t.carType, t.Capacity, t.ExpiryTime, t.CompanyName, t.DriverName, t.DriverMobile FROM View_PermitTruck p LEFT JOIN View_TruckInfo t ON p.carno = t.carno WHERE 1=1
AND p.CarNo = #{carNo}
and p.EndTime >= #{time}
AND p.permitNo = #{permitNo}
ORDER BY p.EndTime DESC;

select * from (select top ${size} * from (select top ${page} * from ( SELECT top 1000 * FROM ( SELECT p.ProjectId, p.PermitNo, p.LineName, p.ProjectName, pt.EndTime FROM View_ProjectPermit p LEFT JOIN View_PermitTruck pt ON p.PermitNo = pt.PermitNo WHERE 1 = 1
and p.ProjectName LIKE '%'+ #{projectName}+'%'
and pt.EndTime >= #{time}
and (SELECT CONVERT(VARCHAR(10),pt.EndTime,120)) = #{endTime}
and pt.CarNo LIKE '%'+ #{name}+'%'
GROUP BY p.PermitNo, p.ProjectId, p.LineName, p.ProjectName, pt.EndTime ) as a ORDER BY a.EndTime DESC ) as b order by b.EndTime asc ) as temp order by temp.EndTime DESC ) temp_order order by temp_order.EndTime DESC ;

SELECT ISNULL( p.BeginTime, '' ) BeginTime, ISNULL( p.EndTime, '' ) EndTime, ISNULL( p.LineName, '' ) LineName, ISNULL( p.PermitNo, '' ) PermitNo, ISNULL( p.CompanyName, '' ) CompanyName, ISNULL( p.ProjectName , '' ) ProjectName, ISNULL( p.Absorption, '' ) Absorption, ISNULL( i.CarType, '' ) CarType, ISNULL( i.Capacity, '' ) Capacity, ISNULL( i.ExpiryTime, '' ) ExpiryTime, ISNULL( i.CarNo, '' ) CarNo, ISNULL( i.DriverName, '' ) DriverName, ISNULL( i.DriverMobile , '' ) DriverMobile, ISNULL( p.place, '' ) place, ISNULL( p.mucktype, '' ) mucktype, ISNULL( p.approvetime, '' ) approvetime, ISNULL( p.permittype, '' ) permittype, ISNULL( com.Contract, '' ) Contract, ISNULL( com.Mobile , '' ) Mobile FROM View_PermitInfo p LEFT JOIN View_PermitTruck t ON t.PermitNo= p.PermitNo LEFT JOIN View_TruckInfo i ON i.CarNo= t.CarNo LEFT JOIN View_CompanyInfo com ON com.CompanyName= p.CompanyName WHERE 1 = 1
and t.permitNo= #{projectId}
and t.EndTime >= #{time}
ORDER BY p.EndTime DESC;

SELECT COUNT(*) FROM (SELECT p.PermitNo FROM View_ProjectPermit p LEFT JOIN View_PermitTruck pt ON p.PermitNo = pt.PermitNo WHERE 1 = 1
and p.ProjectName LIKE '%'+ #{projectName}+'%'
and pt.EndTime >= #{time}
and (SELECT CONVERT(VARCHAR(10),pt.EndTime,120)) = #{endTime}
and pt.CarNo LIKE '%'+ #{name}+'%'
GROUP BY p.PermitNo ) AS c
