mapper.select:
SELECT t.modifyUser , t.modifyDate, t.isDelete, t.installtime, t.installstatus, t.deptid, t.carId, t.createUser, t.createDate, t.terminalType, t.terminal, t.terminalId, tt.typename , m.m0331 carNumber FROM M03 m LEFT JOIN terminal_info t ON m.RecId= t.carId LEFT JOIN settings_device_type tt ON t.terminalType= tt.id WHERE 1 =1
and t.deptid=#{deptid}
and t.carId=#{carId}
and t.carNumber like '%'+#{carNumber}+'%'

mapper.update:
update terminal_info set
modifyUser=#{modifyUser}

    ,isDelete=#{isDelete}


    ,terminal=#{terminal}


    ,terminalType=#{terminalType}


    ,modifyDate=#{modifyDate}


    ,carNumber=#{carNumber}

where terminalId=#{terminalId}
